[data-theme="dark"] #loader .loading-bg {
  background-color: rgba(37,37,37,0.9);
}
[data-theme="dark"] #loader .spinner-box .loading-word {
  color: var(--red-1, #ff5252);
}
#loader .loading-bg {
  position: fixed;
  z-index: 1000;
  width: 50%;
  height: 100%;
  background-color: rgba(255,255,255,0.9);
  transition: 0.8s;
}
#loader .loading-right-bg {
  right: 0;
}
#loader .spinner-box {
  position: fixed;
  z-index: 1001;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100vh;
  opacity: 1;
  transition: 0.5s;
}
#loader .spinner-box .loading-taichi {
  opacity: 0.8;
}
#loader .spinner-box .rotate {
  animation: rotate-all 2s linear infinite;
}
#loader .spinner-box .loading-word {
  position: absolute;
  color: var(--red-1, #ff5252);
  top: calc(50% + 110px);
  left: 50%;
  transform: translate(-50%, -50%);
  font: 700 1.2rem  'Mulish','Noto Serif SC', '-apple-system','PingFang SC','Microsoft YaHei','sans-serif';
}
#loader.loading .loading-left-bg {
  transform: translate(-100%, 0);
}
#loader.loading .loading-right-bg {
  transform: translate(100%, 0);
}
#loader.loading .spinner-box {
  opacity: 0;
  visibility: hidden;
}
@keyframes rotate-all {
  100% {
    transform: rotate(360deg);
  }
}

Page._content:
.outer-box {
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  margin: 10px 0;

  --heat-level-0: unquote("color-mix(in srgb, var(--red-0) 10%, white)");
  --heat-level-1: unquote("color-mix(in srgb, var(--red-0) 40%, white)");
  --heat-level-2: unquote("color-mix(in srgb, var(--red-0) 80%, white)");
  --heat-level-3: var(--red-0);
  --heat-level-4: unquote("color-mix(in srgb, var(--red-0) 80%, black)");
}

[data-theme="dark"] .outer-box {
  --heat-level-0: #161b22;
  --heat-level-1: unquote("color-mix(in srgb, var(--red-0) 30%, transparent)");
  --heat-level-2: unquote("color-mix(in srgb, var(--red-0) 50%, transparent)");
  --heat-level-3: unquote("color-mix(in srgb, var(--red-0) 70%, transparent)");
  --heat-level-4: var(--red-0);
}

.calendar-container {
  display: grid;
  grid: auto repeat(7, 10px) auto / auto repeat(53, 10px);
  gap: 3px;


  width: fit-content;
  font-size: 12px;
  padding: 14px;
  border: solid 1px var(--color-border);
  border-radius: 0.375rem;
}

.month {
  grid-row: 1/2;
  margin-bottom: -3px;
}

.week {
  grid-row: 3;
  grid-column: 1/2;
  line-height: 10px;
  margin-right: 3px;
}
.week + .week {
  grid-row: 5;
}
.week + .week + .week {
  grid-row: 7;
}


.tiles {
  grid-column: 2/55;
  grid-row: 2/9;


  display: grid;
  grid-auto-flow: column;
  grid-template: subgrid / subgrid;
}


.tile {
  width: 10px;
  height: 10px;
  border-radius: 2px;
  outline: 1px solid unquote("color-mix(in srgb, var(--red-2) 10%, transparent)");
  outline-offset: -1px;
}
.tile[data-level="0"] {
  background: var(--heat-level-0);
  // outline: none;
}
.tile[data-level="1"] {
  background: var(--heat-level-1);
}
.tile[data-level="2"] {
  background: var(--heat-level-2);
}
.tile[data-level="3"] {
  background: var(--heat-level-3);
}
.tile[data-level="4"] {
  background: var(--heat-level-4);
}

i.tile:hover {
  outline: 1px solid var(--red-2);
}

line-height = 1.8em;

#heatmap-tooltip {
  position: fixed;
  z-index: 1000;
  padding: 5px;
  background-color: var(--color-background);
  border-radius: 0.375rem;
  border: solid 1px var(--color-border);
  box-shadow: var(--shadow-meta);
  font-size: 12px;
  color: var(--color-default);
  max-width: 250px;
  display: none; /* Initially hidden */


  ul {
    margin: 0 20px;
    line-height: line-height;
    list-style: disc;
    margin-top: (line-height / 2);
    margin-bottom: (line-height / 2);
  }

  li {
    &:hover {
      &::marker {
        transition: color 0.2s;
        color: var(--red-1);
      }
    }
  }

  a {
    color: var(--color-link);
    text-decoration: none;
    word-break: break-word;

    &:hover {
      text-decoration: underline;
    }
  }

  .tooltip-header {
    display: flex;
    justify-content: space-between;
    align-items: center;

    .tooltip-header-content {
      font-weight: bold;
      font-size: 16px;
      color: var(--color-default);
    }

    .tooltip-close {
      padding: 0 5px;
      color: var(--color-link);
      font-weight: bold;
      font-size: 12px;
      cursor: var(--cursor-pointer);
    }
  }

  .tooltip-content {
    max-height: 300px;
    overflow-y: auto;
    line-height: 1.2;
  }
}

.total {
  grid-column: 2/30;
  grid-row: 9;
  margin-top: 4px;
}


.legend {
  grid-column: 30/53;
  grid-row: 9;
  margin-top: 4px;


  display: flex;
  gap: 5px;
  justify-content: right;
  align-items: center;
}


.year-select {
  height: 140px;
  padding: 5px;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  gap: 4px;
  flex-shrink: 0;
}


.year-option {
  padding: 5px 20px;
  border-radius: 0.375rem;
  cursor: var(--cursor-pointer) !important;
}


.year-option:hover {
  background-color: var(--heat-level-1);
}


.year-option.active {
  background-color: var(--heat-level-2);
  color: white;
}


.inner-box {
  overflow: auto;


  ::-webkit-scrollbar {
    width: 5px;
    height: 5px;
  }
}


@media screen and (max-width: 768px) {
  .year-select {
    width: 100%;
    height: auto;
    flex-direction: row;
    justify-content: start;
    align-items: center;
  }


  .outer-box {
    flex-direction: column;
  }


  .inner-box {
    width: 100%;
    overflow-x: auto;
  }
}

