html,
body,
#container {
  height: 100%;
  scroll-behavior: smooth;
  cursor: var(--cursor-default);
}
body {
  width: 100%;
  background: var(--color-background);
  -webkit-text-size-adjust: 100%;
  -webkit-font-smoothing: antialiased;
  text-size-adjust: 100%;
  font-family: 'Mulish','Noto Serif SC', '-apple-system','PingFang SC','Microsoft YaHei','sans-serif';
  transition: background 1s;
  color: var(--color-default);
}
body ::selection {
  background: var(--red-1);
  color: #fff;
}
a,
button {
  cursor: var(--cursor-pointer) !important;
}
.site-search input {
  cursor: var(--cursor-text) !important;
}
#main span:not(button span),
#main p:not(button p),
#main code,
#main kbd,
#main input,
#main textarea {
  cursor: var(--cursor-text) !important;
}
#main nav span {
  cursor: var(--cursor-default) !important;
}
#container {
  position: relative;
}
.mobile-nav-on {
  overflow: hidden !important;
}
#wrap {
  position: absolute;
  z-index: 1;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  transition: 1s;
  background: var(--color-background);
}
#main {
  flex: 1;
  padding: 0 10px;
  max-width: 100%;
  box-sizing: border-box;
}
@media screen and (max-width: 959px) {
  #main {
    min-width: 100%;
  }
}
#content {
  position: relative;
  display: flex;
  padding-top: 5px;
  align-items: stretch;
  max-width: 1350px;
  margin: 0 auto;
  overflow: visible;
}
@media screen and (min-width: 960px) {
  .sidebar-left #main,
  .sidebar-right #main {
    padding: 0;
    width: 73%;
    margin: 0 1%;
  }
}
#content.sidebar-right {
  flex-direction: row-reverse;
}
#mask {
  z-index: 1000;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0,0,0,0.3);
  backdrop-filter: blur(3px);
  -webkit-backdrop-filter: blur(3px);
  opacity: 1;
  transition: 0.3s;
}
#mask.hide {
  opacity: 0;
  visibility: hidden;
}
#header {
  min-height: 400px;
  height: 35vh;
  position: relative;
}
#header img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 150%;
  z-index: -2;
  user-select: none;
  display: block;
  object-fit: cover;
  transition: 0.3s;
  -webkit-mask: linear-gradient(to top, transparent, #fff 50%);
  mask: linear-gradient(to top, transparent, #fff 50%);
}
@media screen and (max-width: 959px) {
  #header img {
    height: 120%;
  }
}
@media screen and (max-width: 767px) {
  #header img {
    height: 100%;
  }
}
#header-outer {
  height: 100%;
