}
[data-theme="dark"] #header-nav:hover {
  background: var(--color-header-background);
}
#header-nav {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  display: flex;
  justify-content: center;
  background: var(--color-header-background);
  box-shadow: var(--shadow-card);
  transition: background 1s, top 0.3s;
  z-index: 200;
}
