                switch (dessert) {
                    case 'cake':
                        message = "🍰 蛋糕代表甜蜜生活！今天会有令人开心的小惊喜，记得留意身边的美好事物哦~";
                        break;
                    case 'icecream':
                        message = "🍦 冰淇淋代表清凉心情！今天适合放松自己，做些让自己开心的事情，别太劳累~";
                        break;
                    case 'chocolate':
                        message = "🍫 巧克力代表浪漫爱情！今天感情运势上升，适合表达心意或安排甜蜜约会~";
                        break;
                    case 'cookie':
                        message = "🍪 饼干代表温馨日常！今天适合与家人朋友共度美好时光，享受简单的小幸福~";
