#content.sidebar-right {
  flex-direction: row-reverse;
}
#mask {
  z-index: 1000;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0,0,0,0.3);
  backdrop-filter: blur(3px);
  -webkit-backdrop-filter: blur(3px);
  opacity: 1;
  transition: 0.3s;
}
#mask.hide {
  opacity: 0;
  visibility: hidden;
}
#header {
  min-height: 400px;
  height: 35vh;
  position: relative;
}
#header img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 150%;
  z-index: -2;
  user-select: none;
  display: block;
  object-fit: cover;
  transition: 0.3s;
  -webkit-mask: linear-gradient(to top, transparent, #fff 50%);
  mask: linear-gradient(to top, transparent, #fff 50%);
}
@media screen and (max-width: 959px) {
  #header img {
    height: 120%;
  }
}
@media screen and (max-width: 767px) {
  #header img {
