  box-shadow: 0 0 5px 2px var(--color-tip-shadow);
  border-left: 6px var(--color-tip-text) solid;
}
.custom-block.tip .custom-block-title:before {
  font-weight: normal;
  font-family: "iconfont";
  content: "\e643";
