quint8 OtaWorker::calculatePid(quint8 id) {
    id &= 0x3F; // 确保6位ID
    quint8 p0 = ((id >> 0) & 1) ^ ((id >> 1) & 1) ^ 
                ((id >> 2) & 1) ^ ((id >> 4) & 1);
    quint8 p1 = ~(((id >> 1) & 1) ^ ((id >> 3) & 1) ^ 
                 ((id >> 4) & 1) ^ ((id >> 5) & 1)) & 1;
    return id | (p0 << 6) | (p1 << 7);
}
